`timescale 1ns / 1ps
module avg_layer_tb ;
parameter EXPONENT_WIDTH = 8;
parameter MANTISSA_WIDTH = 23;
parameter dimension = 4 ;
parameter dimension2 = (dimension/2)  ;
parameter DATA_WIDTH = EXPONENT_WIDTH+MANTISSA_WIDTH+1;
reg [(dimension*dimension*DATA_WIDTH)-1:0] input_value;
reg reset ;
reg clk;
wire [(dimension2*dimension2*DATA_WIDTH)-1:0] output_value;

initial
begin
clk =1;
reset = 0;
input_value = 512' ;
//[2  3  1  2]
//[4  5  3  4]
//[-1 -2 -2 -3] 
//[-3 -4 -4 -5]

#8 reset = 1 ;
#2 reset = 0 ;
input_value=512';
 
#8 reset = 1 ;
#2 reset = 0 ;
input_value = 512';
//[13 -5 -8  2]
//[100 1250 10 1]`
//[4 5 6 7]   -->  [7.75   11]
//[10 12 15 16]     -->[339.5    1.25]

#25 $stop;
end
always begin
     #1  clk = ~clk; 
    end

avg_layer #(.EXPONENT_WIDTH(EXPONENT_WIDTH), .MANTISSA_WIDTH(MANTISSA_WIDTH), .dimension(dimension),
.DATA_WIDTH(DATA_WIDTH)) inst1 (
.img_in(input_value),
.clk(clk),
.img_out(output_value),
.reset(reset)
);
endmodule