`timescale 1ns / 1ps


module avg_pooling_input_tensor_tb;
parameter EXPONENT_WIDTH = 8;
parameter MANTISSA_WIDTH = 23;
parameter dimension = 4 ;
parameter dimension2 = (dimension/2)  ;
parameter DATA_WIDTH = EXPONENT_WIDTH+MANTISSA_WIDTH+1;
parameter filter =3;
reg [(filter*dimension*dimension*DATA_WIDTH)-1:0] input_value;
reg reset ;
reg clk;
wire [(filter*dimension2*dimension2*DATA_WIDTH)-1:0] output_value;
initial
begin
clk =1;
reset = 0;
#200 reset=1;
input_value=1536';
#2 reset =0;

#80 $stop;
end
always begin
     #1  clk = ~clk; 
    end

avg_pooling_input_tensor #(.EXPONENT_WIDTH(EXPONENT_WIDTH), .MANTISSA_WIDTH(MANTISSA_WIDTH), .dimension(dimension),
.DATA_WIDTH(DATA_WIDTH),.filter(filter)) inst1 (
.imgs_in(input_value),
.clk(clk),
.reset_module(reset),
.imgs_out(output_value)
);

endmodule
